                if (checkbox_policy.checked == true) {
                    checkbox_policy.checked = false;
                }
                else {
                    checkbox_policy.checked = true;
                }
                check();
            });

            function closeForm() {
                document.querySelector('body').style.overflow = "unset";
                fadeOut(popup, 1000);
            };

            function openForm() {
                check();
                fadeIn(popup, 1000, "flex");
            };

            const fadeIn = (el, timeout, display) => {
                el.style.opacity = 0;
                el.style.display = display || 'block';
                el.style.transition = `opacity ${timeout}ms`;
                setTimeout(() => {
                    el.style.opacity = 1;
                }, 10);
            };

            const fadeOut = (el, timeout) => {
                el.style.opacity = 1;
                el.style.transition = `opacity ${timeout}ms`;
                el.style.opacity = 0;
                setTimeout(() => {
                    el.style.display = 'none';
                }, timeout);
            };

            function check () {
                if (checkbox_location.checked == true && checkbox_policy.checked == true && input_number.value.length == 8) {
                    button_submit.disabled = false;
                }
                else {
                    button_submit.disabled = true;
                }
