            form.addEventListener('submit', function() {
                timeout = 1000;
                fadeOut(document.querySelector('.popup-block'), timeout);
                setTimeout(() => {
                    if (window.innerWidth <= 1000) {
                        document.querySelector('.base-popup').style.background = "unset";
                        document.querySelector('body').style.overflow = "hidden";
                    }
                    fadeIn(document.querySelector('.popup-submited'), timeout, "block");
                }, timeout);
                
            });

        
            if (window.innerWidth > 1000 && window.innerWidth / window.innerHeight > 2.0317) {
