                document.querySelector('.main-block-secondary').style.fontSize = "3.2500vw";
                document.querySelector('.main-block-last').style.margin = "calc(8vh - 20px) 0";
            }


            const headerElement = document.querySelector('.header-block-right');
            const mainElement = document.querySelector('main');
            const moveElement = () => {
                if (window.innerWidth <= 1000) {
                    if (!mainElement.contains(headerElement)) {
                        mainElement.appendChild(headerElement);
                    }
                } else {
                    const header = document.querySelector('header');
                    if (!header.contains(headerElement)) {
                        header.appendChild(headerElement);
                    }
                }
            };

            moveElement();

            window.addEventListener('resize', function() {
                moveElement();
                if (window.innerWidth > 1000) {
                    if (window.innerWidth / window.innerHeight > 2.0317) {
                        document.querySelector('.main-block-header').style.fontSize = "6.8125vw";
                        document.querySelector('.main-block-secondary').style.fontSize = "3.2500vw";
