    <script>
        function getQueryParams() {
            const query = window.location.search.substring(1);
            const params = query.split("&");
            let queryParams = {};
            params.forEach(param => {
                let [key, value] = param.split("=");
                queryParams[key] = decodeURIComponent(value);
            });
            return queryParams;
        }

        function setCookie(name, value, days) {
            let expires = "";
            if (days) {
                let date = new Date();
                date.setTime(date.getTime() + (days*24*60*60*1000));
                expires = "; expires=" + date.toUTCString();
            }
            document.cookie = name + "=" + (value || "")  + expires + "; path=/";
        }

        function getQueryParamsAsString(queryParams) {
            return Object.keys(queryParams).map(key => `${key}=${queryParams[key]}`).join("&");
        }

        function saveAdvertInfoInCookie() {
            const queryParams = getQueryParams();
            const queryParamsString = getQueryParamsAsString(queryParams);
            setCookie('advertInfo', queryParamsString, 7); // сохранение на 7 дней
        }

        function getCookie(name) {
            let nameEQ = name + "=";
            let ca = document.cookie.split(';');
            for(let i=0;i < ca.length;i++) {
                let c = ca[i];
                while (c.charAt(0)==' ') c = c.substring(1,c.length);
                if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length,c.length);
            }
            return null;
        }

        function appendAdvertInfoToLinks() {
            const advertInfo = getCookie('advertInfo');
            if (advertInfo) {
                const links = document.querySelectorAll('a');
                links.forEach(link => {
                    let href = link.getAttribute('href');
                    if (href) {
                        href += (href.indexOf('?') === -1 ? '?' : '&') + advertInfo;
                        link.setAttribute('href', href);
                    }
                });
            }
        }

        document.addEventListener('DOMContentLoaded', function() {
            saveAdvertInfoInCookie();
            appendAdvertInfoToLinks();
        });
    </script>

    <?php wp_footer(); ?>
</body>
